package com.witmob.night.ui;import netlib.util.ResourceUtil;import android.os.Bundle;import android.support.v4.app.FragmentActivity;import android.view.Window;/** * @author henzil * @version create time:2014-8-31_下午2:28:43 * @Description 所有fragmentactivity base类 */public abstract class BaseFragmentActivity extends FragmentActivity {	protected String TAG;	protected ResourceUtil resourceUtil;	@Override	protected void onCreate(Bundle arg0) {		super.onCreate(arg0);		initBaseData(arg0);		initBaseViews();	}	protected void initBaseData(Bundle savedInstanceState) {		resourceUtil = ResourceUtil.getInstance(getApplicationContext());		TAG = toString();	}	protected void initBaseViews() {		requestWindowFeature(Window.FEATURE_NO_TITLE);	}	protected abstract void initData();	protected abstract void initViews();	protected abstract void initWidgetActions();}