package com.witmob.night.ui.fragment;import netlib.util.ResourceUtil;import android.app.Fragment;import android.content.Context;import android.os.Bundle;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;/** * @author henzil * @version create time:2014-8-31_下午4:03:53 * @Description TODO */public abstract class BaseFragment extends Fragment{	protected String TAG;	protected ResourceUtil resourceUtil;		protected Context context;		@Override	public void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		initBaseData(savedInstanceState);	}	protected void initBaseData(Bundle savedInstanceState) {		context = getActivity();		resourceUtil = ResourceUtil.getInstance(context);		TAG = toString();	}	protected abstract void initData();	protected abstract View initViews(LayoutInflater inflater, ViewGroup container, Bundle savedInstanceState);	protected abstract void initWidgetActions();	}